import { Component, OnInit } from '@angular/core';
import { Location } from '@angular/common';
import { ActivatedRoute } from '@angular/router';
// Import Services
import { EventoService } from '../../services/evento.service';
import { Detalle_eventoService } from '../../services/detalle_evento.service';
import { ServicioService } from '../../services/servicio.service';
// Import Models
import { Evento } from '../../domain/newtest_db/evento';
import { Servicio } from '../../domain/newtest_db/servicio';
import { Detalle_evento } from '../../domain/newtest_db/detalle_evento';

