/**
 * This component allows to edit a Evento
 */
@Component({
    selector: 'app-evento-edit',
    templateUrl: 'evento-edit.component.html',
    styleUrls: ['evento-edit.component.css']
})
export class EventoEditComponent implements OnInit {
    item: Evento;
    listEvento_servicio: Servicio[];
    listId_evento: Evento[];
    externalDetalle_evento: Detalle_evento[];
    model: Evento;
    formValid: Boolean;

    constructor(
    private eventoService: EventoService,
    private detalle_eventoService: Detalle_eventoService,
    private servicioService: ServicioService,
    private route: ActivatedRoute,
    private location: Location) {
        // Init item
        this.item = new Evento();
        this.externalDetalle_evento = [];
    }

    /**
     * Init
     */
    ngOnInit() {
        this.route.params.subscribe(param => {
            const id: string = param['id'];
            if (id !== 'new') {
                this.eventoService.get(id).subscribe(item => this.item = item);
                this.detalle_eventoService.findById_evento(id).subscribe(list => this.externalDetalle_evento = list);
            }
            // Get relations
            this.servicioService.list().subscribe(list => this.listEvento_servicio = list);
        });
    }


    /**
     * Save Evento
     *
     * @param {boolean} formValid Form validity check
     * @param Evento item Evento to save
     */
    save(formValid: boolean, item: Evento): void {
        this.formValid = formValid;
        if (formValid) {
            if (item._id) {
                this.eventoService.update(item).subscribe(data => this.goBack());
            } else {
                this.eventoService.create(item).subscribe(data => this.goBack());
            } 
        }
    }

    /**
     * Go Back
     */
    goBack(): void {
        this.location.back();
    }


}



